mapper.update:
MERGE INTO TUSER T
USING (
	SELECT 
		#{MAIL} AS MAIL
	FROM DUAL
) T2
ON (
	T.MAIL = T2.MAIL
)
 WHEN MATCHED THEN
      UPDATE
         
            PASS_WD = #{PASS_WD},
            LAST_NM = #{LAST_NM},
            FRST_NM = #{FRST_NM},
            REGI_NO = #{REGI_NO},
            CARD_NO = #{CARD_NO},
            ACCT_NO = #{ACCT_NO},
            CELL_NO = #{CELL_NO},
            TELE_NO = #{TELE_NO},
            BRTH_DT = #{BRTH_DT},
            ADDR = #{ADDR},
         
 WHEN NOT MATCHED THEN
      INSERT (
             MAIL
            ,PASS_WD 
            ,LAST_NM
            ,FRST_NM
            ,REGI_NO
            ,CARD_NO
            ,ACCT_NO
            ,CELL_NO
            ,TELE_NO
            ,BRTH_DT
            ,ADDR
      ) VALUES (
             #{MAIL}
            ,#{PASS_WD}
            ,#{LAST_NM}
            ,#{FRST_NM}
            ,#{REGI_NO}
            ,#{CARD_NO}
            ,#{ACCT_NO}
            ,#{CELL_NO}
            ,#{TELE_NO}
            ,#{BRTH_DT}
            ,#{ADDR}
      )

mapper.insert:
INSERT INTO TUSER (
      MAIL
      , PASS_WD
      , LAST_NM
      , FRST_NM
      , REGI_NO
      , CARD_NO
      , ACCT_NO
      , CELL_NO
      , TELE_NO
      , BRTH_DT
      , ADDR
) VALUES (
      #{MAIL}
      , #{PASS_WD}
      , #{LAST_NM}
      , #{FRST_NM}
      , #{REGI_NO}
      , #{CARD_NO}
      , #{ACCT_NO}
      , #{CELL_NO}
      , #{TELE_NO}
      , #{BRTH_DT}
      , #{ADDR}
)

mapper.update:
UPDATE TUSER
  SET PASS_WD = #{PASS_WD}
       , LAST_NM = #{LAST_NM}
       , FRST_NM = #{FRST_NM}
       , REGI_NO = #{REGI_NO}
         , CARD_NO = #{CARD_NO}
       , ACCT_NO = #{ACCT_NO}
       , CELL_NO = #{CELL_NO}
       , TELE_NO = #{TELE_NO}
       , BRTH_DT = #{BRTH_DT}
       , ADDR = #{ADDR}
WHERE MAIL = #{MAIL}
